    private void dfs(char[][] board, int x, int y) {
    /*
     * here we use condition board[x][y] != 'O'. if the element is 'X', we
     * cannot move forward because it's not a surrounded element. if the
     * element is 'D', we also cannot move forward because it has been
     * visited (and thus marked), otherwise there would be stack overflow.
     */
    if (x < 0 || x >= board.length || y < 0 || y >= board.length
        || board[x][y] != 'O')
        return;

    board[x][y] = 'D';
    dfs(board, x - 1, y);
    dfs(board, x + 1, y);
    dfs(board, x, y - 1);
    dfs(board, x, y + 1);
    }

    public void solve(char[][] board) {
    if (board == null || board.length == 0)
        return;

    /*
     * we move from the border elements.
     */
    for (int j = 0; j < board.length; j++) {
        dfs(board, 0, j);
        dfs(board, board.length - 1, j);
    }

    for (int i = 1; i < board.length - 1; i++) {
        dfs(board, i, 0);
        dfs(board, i, board.length - 1);
    }

    /*
     * if the element is 'D' (previously marked), it can be visited from an
     * border element, which means it's not surrounded. if the element
     * remains 'X', it cannot be visited since it's surrounded.
     */
    for (int i = 0; i < board.length; i++) {
        for (int j = 0; j < board.length; j++) {
        if (board[i][j] == 'O')
            board[i][j] = 'X';
        else if (board[i][j] == 'D')
            board[i][j] = 'O';
        }
    }
    }

    private Queue<Integer> queue = new LinkedList<Integer>();
    private void fill(char[][] board, int x, int y) {
    if (x < 0 || x >= board.length || y < 0 || y >= board.length
        || board[x][y] != 'O')
        return;
    
    /*
     * very good workaround to use existing container to place pair(x, y).
     */
    queue.offer(x * board.length + y);
    board[x][y] = 'D';
    }

    private void bfs(char[][] board, int x, int y) {
    /*
     * for each element, we will see if we can push it to the queue and 
     * then check its surrounding elements, until there is no elements
     * available in the queue, which means there is no way to move forward.
     */
    fill(board, x, y);

    while (!queue.isEmpty()) {
        int curr = queue.poll();
        int i = curr / board.length;
        int j = curr % board.length;

        fill(board, i - 1, j);
        fill(board, i + 1, j);
        fill(board, i, j - 1);
        fill(board, i, j + 1);
    }
    }

    public void solve_bfs(char[][] board) {
    for (int i = 0; i < board.length; i++) {
        bfs(board, 0, i);
        bfs(board, board.length - 1, i);
    }
    for (int i = 1; i < board.length - 1; i++) {
        bfs(board, i, 0);
        bfs(board, i, board.length - 1);
    }

    for (int i = 0; i < board.length; i++) {
        for (int j = 0; j < board.length; j++) {
        if (board[i][j] == 'D')
            board[i][j] = 'O';
        else if (board[i][j] == 'O')
            board[i][j] = 'X';
        }
    }
    }

    public char[][] init(String[] chars) {
    char[][] board = new char[chars.length][];

    for (int i = 0; i < chars.length; i++) {
        String str = chars[i];
        board[i] = new char[str.length()];
        for (int j = 0; j < str.length(); j++) {
        board[i][j] = str.charAt(j);
        }
    }

    return board;
    }
